      { id: "qft-9", type: "multiple-choice", question: "What is spontaneous symmetry breaking?", options: [{ value: "a", text: "Symmetry of laws vs. solutions" }, { value: "b", text: "Gives particles mass" }, { value: "c", text: "Higgs mechanism" }, { value: "d", text: "All of the above" }], correctAnswer: "d", explanation: "Spontaneous symmetry breaking through Higgs field gives particles mass.", concept: "Broken symmetries" },
      { id: "qft-10", type: "multiple-choice", question: "What are gauge theories?", options: [{ value: "a", text: "Theories with local symmetries" }, { value: "b", text: "Describe fundamental forces" }, { value: "c", text: "Basis of Standard Model" }, { value: "d", text: "All of the above" }], correctAnswer: "d", explanation: "Gauge theories with local symmetries form the foundation of particle physics.", concept: "Local symmetry principles" }
